    } else if (unidade >= 2 && nomeProduto !== "" && el.target.outerText === '-') {
      setUnidade(unidade - 1)
    }
  }

  function adicionarProduto() {
    if (nomeProduto !== "") {
      const prodBool = listaProdutos.find(
        (el) => el[0] === nomeProduto
      );
      if (listaProdutos.find((el) => el[0] === nomeProduto)) {
