        prodBool[2] = Number(prodBool[1] * precoProduto.slice(2)).toFixed(2)
      } else {
        listaProdutos.push([nomeProduto, unidade, Number(unidade * precoProduto.slice(2)).toFixed(2)]);
      }
      setLista(listaProdutos)
      setUnidade(1)
      setPreco("R$ 0.00")
      setNome("")
    }
  }
  return (
    <div className="cadastro__produtos">
